import React, { useState } from "react";
import TextField from '@mui/material/TextField';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DateTimePicker from '@mui/lab/DateTimePicker';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Button from '@mui/material/Button';
import { Controller, useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux'
import create from 'features/history/reducer/historySlice'


export default function TimelineAdd() {
    const defaultValues = {
        id: 1, //로그인 되면 id 넣기
        log_type: '',
        contents: '',
        location: '',
        address: '',
        log_date: '',
        weather: '',
    };
    const dispatch = useDispatch()

    const { control, formState, handleSubmit, reset, getValues } = useForm({
        mode: 'onChange',
        defaultValues,
    });
    function onSubmit() {
        reset(defaultValues);
    }


    const [mode, setMode] = useState(0)
    const [value, setValue] = useState(0)
    const today = new Date()
    return (<>
        {mode == 0 ? <>
            <Button variant="outlined" onClick={() => setMode(1)}>기록 추가</Button>
        </>
            : <Box
                component="form"
                sx={{
                    '& > :not(style)': { m: 1 },
                }}
                noValidate
                autoComplete="off"
                // onSubmit={handleSubmit(async (data) => { await dispatch(create({
                //     ...data,
                //    })) })}
                onSubmit={handleSubmit(async(data)=>alert(JSON.stringify(data)))}
                >
                <LocalizationProvider dateAdapter={AdapterDateFns} sx={{ width: '20ch' }}>
                    <DateTimePicker
                        renderInput={(params) => <TextField {...params} />}
                        value={today}
                        onChange={(newValue) => {
                            setValue(newValue);
                        }}
                    />
                </LocalizationProvider>
                    <FormControl sx={{ width: '15ch' }}>
                        <InputLabel id="weather-select-label">날씨</InputLabel>
                        <Select
                            labelId="weather-select-label"
                            id="weather-select"
                            // value={value}
                            label="weather"
                        // onChange={handleChange}
                        >
                            <MenuItem value={'맑음'}>맑음</MenuItem>
                            <MenuItem value={'구름 많음'}>구름 많음</MenuItem>
                            <MenuItem value={'흐림'}>흐림</MenuItem>
                            <MenuItem value={'비'}>비</MenuItem>
                            <MenuItem value={'비/눈'}>비/눈</MenuItem>
                            <MenuItem value={'눈'}>눈</MenuItem>
                            <MenuItem value={'소나기'}>소나기</MenuItem>
                            <MenuItem value={'데이터 없음'}>모르겠음!</MenuItem>
                        </Select>
                    </FormControl>
                    <FormControl sx={{ width: '15ch' }}>
                        <InputLabel id="category-select-label">카테고리</InputLabel>
                        <Select
                            labelId="category-select-label"
                            id="category-select"
                            // value={value}
                            label="category"
                        // onChange={handleChange}
                        >
                            <MenuItem value={'visit'}>방문</MenuItem>
                            <MenuItem value={'payment'}>결제 내역</MenuItem>
                        </Select>
                    </FormControl>
                    <TextField id="outlined-basic" label="장소" variant="outlined" sx={{ width: '20ch' }} placeholder="방문 장소" />
                    <TextField id="outlined-basic" label="주소" variant="outlined" sx={{ width: '20ch' }} placeholder="상세 주소" />
                    <br />
                    <Controller
                    name="contents"
                    control={control}
                    render={({ field }) => (
                    <TextField 
                        {...field}
                        type="text"
                        id="outlined-basic" label="내용" variant="outlined" 
                        sx={{ width: '100.5ch' }} placeholder="무엇을 했다!" 
                    />
                    )}/>
                    {/* <Controller
                    name="contents"
                    control={control}
                    render={({ field }) => (
                      <TextField
                        {...field}
                        className="mb-16"
                        label="address"
                        type="address"
                        error={!!errors.address}
                        helperText={errors?.address?.message}
                        variant="outlined"
                        fullWidth
                      />
                    )}
                  /> */}
                    <br />
                    <Button type="submit" variant="text" >작성 완료</Button>
                    <Button variant="text" onClick={() => setMode(0)}>취소 하기</Button>

            </Box>}
    </>)
}
